import { Component } from '@angular/core';

@Component({
  selector: 'app-emitter',
  templateUrl: './emitter.component.html',
  styleUrls: ['./emitter.component.scss'],
})
export class EmitterComponent {
  myNumber: number = 0;

  onChangeNumber() {
    this.myNumber = Math.floor(Math.random() * 50);
  }
}
<div class="class">
  <h1>O número é: {{ myNumber }}</h1>
  <app-chage-number (changeNumber)="onChangeNumber()"></app-chage-number>
</div>
