import { Component, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-chage-number',
  templateUrl: './chage-number.component.html',
  styleUrls: ['./chage-number.component.scss'],
})
export class ChageNumberComponent {
  @Output()
  changeNumger: EventEmitter<any> = new EventEmitter();

  handleClick() {
    this.changeNumger.emit();
  }
}
<button (click)="handleClick()">Alterar o número!</button>
