import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-parent-data',
  templateUrl: './parent-data.component.html',
  styleUrls: ['./parent-data.component.scss'],
})
export class ParentDataComponent {
  @Input()
  name!: string;
  @Input()
  userData!: { email: string; role: string };
}
<div class="class">
  <h1>
    Descrevendo usuário: Com @Input esse é o component filho, o pai passa os
    valores, e o componenet recebe
  </h1>
  <p>Nome: {{ name }}</p>
  <p>Email: {{ userData.email }}</p>
  <p>Função do usuário: {{ userData.role }}</p>
</div>
